mapper.select:
SELECT A.SVIN_ID , A.SVIN_TITLE , A.SVIN_START_DT , A.SVIN_END_DT , A.SVIN_OPEN_YN , A.SVIN_USE_YN , A.SVIN_DPLCTN_YN , A.SVIN_RESOPEN_YN , A.SVIN_REG_DTTM , A.SVIN_REG_ID , A.SVIN_REG_IP , (
CASE (CASE WHEN (DATE_FORMAT(NOW(), '%Y-%m-%d') >= A.SVIN_START_DT AND DATE_FORMAT(NOW(), '%Y-%m-%d') <= A.SVIN_END_DT) THEN 'TRUE' ELSE 'FALSE' END) WHEN 'TRUE' THEN 'ING' ELSE (CASE WHEN (DATE_FORMAT(NOW(), '%Y-%m-%d') > A.SVIN_END_DT) THEN 'COMPLETE' ELSE 'PRE' END) END
) AS progress , ( SELECT COUNT(SVIN_ID) FROM COING_SURVEY_VOTE WHERE SVIN_ID = A.SVIN_ID ) AS voteCnt FROM COING_SURVEY_INFO A WHERE 1=1
ORDER BY A.SVIN_ID DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT A.SVIN_ID , A.SVIN_TITLE , A.SVIN_CONTENT , A.SVIN_START_DT , A.SVIN_END_DT , A.SVIN_OPEN_YN , A.SVIN_USE_YN , A.SVIN_DPLCTN_YN , A.SVIN_RESOPEN_YN , A.SVIN_REG_DTTM , A.SVIN_REG_ID , A.SVIN_REG_IP , A.SVIN_UPDT_DTTM , A.SVIN_UPDT_ID , A.SVIN_UPDT_IP , (
CASE (CASE WHEN (DATE_FORMAT(NOW(), '%Y-%m-%d') >= A.SVIN_START_DT AND DATE_FORMAT(NOW(), '%Y-%m-%d') <= A.SVIN_END_DT) THEN 'TRUE' ELSE 'FALSE' END) WHEN 'TRUE' THEN 'ING' ELSE (CASE WHEN (DATE_FORMAT(NOW(), '%Y-%m-%d') > A.SVIN_END_DT) THEN 'COMPLETE' ELSE 'PRE' END) END
) AS progress , ( SELECT COUNT(SVIN_ID) FROM COING_SURVEY_VOTE WHERE SVIN_ID = A.SVIN_ID ) AS voteCnt FROM COING_SURVEY_INFO A WHERE A.SVIN_ID = #{svinId} AND A.SVIN_DEL_YN = 'N'

mapper.insert:
INSERT
  INTO  COING_SURVEY_INFO
     (  SVIN_ID
     ,  SVIN_TITLE
     ,  SVIN_CONTENT
     ,  SVIN_START_DT
     ,  SVIN_END_DT
     ,  SVIN_OPEN_YN
     ,  SVIN_USE_YN
     ,  SVIN_DPLCTN_YN
     ,  SVIN_RESOPEN_YN
     ,  SVIN_REG_DTTM
     ,  SVIN_REG_ID
     ,  SVIN_REG_IP
     )
VALUES
     (  #{svinId, jdbcType=VARCHAR}
	 ,  #{svinTitle, jdbcType=VARCHAR}
	 ,  #{svinContent, jdbcType=VARCHAR}
	 ,  #{svinStartDt, jdbcType=VARCHAR}
	 ,  #{svinEndDt, jdbcType=VARCHAR}
	 ,  #{svinOpenYn, jdbcType=VARCHAR}
	 ,  #{svinUseYn, jdbcType=VARCHAR}
	 ,  #{svinDplctnYn, jdbcType=VARCHAR}
	 ,  #{svinResopenYn, jdbcType=VARCHAR}
	 ,  NOW()
	 ,  #{svinRegId, jdbcType=VARCHAR}
	 ,  #{svinRegIp, jdbcType=VARCHAR}
     )

mapper.update:
UPDATE  COING_SURVEY_INFO
   SET  SVIN_TITLE = #{svinTitle, jdbcType=VARCHAR}
     ,  SVIN_CONTENT = #{svinContent, jdbcType=VARCHAR}
     ,  SVIN_START_DT = #{svinStartDt, jdbcType=VARCHAR}
     ,  SVIN_END_DT = #{svinEndDt, jdbcType=VARCHAR}
     ,  SVIN_OPEN_YN = #{svinOpenYn, jdbcType=VARCHAR}
     ,  SVIN_USE_YN = #{svinUseYn, jdbcType=VARCHAR}
     ,  SVIN_DPLCTN_YN = #{svinDplctnYn, jdbcType=VARCHAR}
     ,  SVIN_RESOPEN_YN = #{svinResopenYn, jdbcType=VARCHAR}
     ,  SVIN_UPDT_DTTM = NOW()
     ,  SVIN_UPDT_ID = #{svinUpdtId, jdbcType=VARCHAR}
     ,  SVIN_UPDT_IP = #{svinUpdtIp, jdbcType=VARCHAR}
 WHERE  SVIN_ID = #{svinId}
   AND  SVIN_DEL_YN = 'N'

UPDATE  COING_SURVEY_INFO
   SET  SVIN_DEL_YN = 'Y'
	 ,  SVIN_DEL_DTTM = NOW()
	 ,  SVIN_DEL_ID = #{svinDelId, jdbcType=VARCHAR}
	 ,  SVIN_DEL_IP = #{svinDelIp, jdbcType=VARCHAR}
 WHERE  SVIN_ID = #{svinId}
   AND  SVIN_DEL_YN = 'N'

