mapper.select:
SELECT A.SVAN_ID , A.SVIN_ID , A.SVVO_ID , A.SVQU_ID , A.SVEX_ID , A.SVAN_REMARKS FROM COING_SURVEY_ANSWER A WHERE A.SVIN_ID = #{svinId} AND A.SVQU_ID = #{svquId}
AND A.SVEX_ID = #{svexId}
ORDER BY A.SVAN_ID DESC
